import React, {useEffect, useState} from 'react'
import Platforms from '../Platforms/Platforms'
import {useSelector, useDispatch} from 'react-redux'
import {LoadGenres, CleanRta, CleanFilterGames} from '../../redux/actions'
import Genres from '../Genres/Genre'
import style from './Form.module.css'
import { validacionesForm } from './validaciones'


export const Form = () => {

  const dispatch = useDispatch()
  const allGenres = useSelector((state) => state.genres)

  const [error, setError]= useState({})
  const [input, setInput] =useState({
    name: '',
    platform: '',
    description: '',
    date: '',
    rating: '',
    searchGenres: [],
    image: null,
    trailer: '',
    website: ''
  })

 
  const handleSubmit =(e)=>{
    e.preventDefault()
        
  }

  const handleChangeInput =(e)=>{
    const name = e.target.name;
    const value = e.target.value;
    const type = e.target.type;

    // Aquí deberías actualizar el estado del componente con el nuevo valor del campo
    // de acuerdo a su nombre (name) y tipo (type)
    if (type === 'file') {
      setInput({
        ...input,
        [name]: e.target.files[0]
      })
    } else if (type === 'checkbox') {
      if (e.target.checked) {
        setInput({
          ...input,
          searchGenres: [...input.searchGenres, value]
        })
      } else {
        setInput({
          ...input,
          searchGenres: input.searchGenres.filter(genre => genre !== value)
        })
      }
    } else {
      setInput({
        ...input,
        [name]: value
      })
    }
  }

  // Aquí deberías escribir la lógica para manejar los cambios en el estado del campo "image"
  const handleChangeImage = (e) => {
    // ...
  }

  useEffect(() => {
    dispatch(LoadGenres()) //Cargar generos al actualiar pagina
    return () => {
      dispatch(CleanRta())
      dispatch(CleanFilterGames())
    }
  }, [dispatch])

  return (
    <form className={style.container} onSubmit={handleSubmit}>
      <div className={style.title}>
        <h1>Crear VideoJuego</h1>
      </div>
      <div>
        <input type="text" placeholder='Nombre' name='name' className={style.inputs} onChange={handleChangeInput}/>
      </div>
      <span></span>
      <div>
        <input type="number" name='rating' placeholder='Clasificación' className={style.inputs} onChange={handleChangeInput}/>
      </div>
      <div>
        <input type="date" name='date' className={style.inputs} onChange={handleChangeInput}/>
      </div>
      <div>
        <textarea name="description" cols="25" rows="4" placeholder='Descripción' className={style.inputs} onChange={handleChangeInput}>  
        </textarea>
      </div>
      <div>
        <select name="platform" onChange={handleChangeInput}>
          <option hidden>
            Seleccionar plataforma
          </option>
          <Platforms/>
        </select>
      </div>
      <div>
        <select name="genres" onChange={handleChangeInput}>
          <option hidden>
            Seleccionar genero
          </option>
          <Genres allGenres={allGenres}/>
        </select>
      </div>
  </form>
  )
}
