import React, { useState } from "react";
import styles from "./Form.module.css";
import Platforms from '../Platforms/Platforms'

export const Form = () => {
  const [nombre, setNombre] = useState("");
  const [fecha, setFecha] = useState("");
  const [clasificacion, setClasificacion] = useState("");
  const [descripcion, setDescripcion] = useState("");
  const [genero, setGenero] = useState("");
  const [plataforma, setPlataforma] = useState("");
  const [imagen, setImagen] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí puedes agregar la lógica para enviar la información del formulario al servidor
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <label>
        Nombre:
        <input
          type="text"
          value={nombre}
          onChange={(event) => setNombre(event.target.value)}
          placeholder="Nombre"
        />
      </label>
      <label>
        Fecha:
        <input
          type="date"
          value={fecha}
          onChange={(event) => setFecha(event.target.value)}
          placeholder="Fecha de Lanzamiento"
        />
      </label>
      <label>
        Clasificación:
        <input
          type="text"
          value={clasificacion}
          onChange={(event) => setClasificacion(event.target.value)}
          placeholder="Clasificación"
        />
      </label>
      <label>
        Descripción:
        <textarea
          value={descripcion}
          onChange={(event) => setDescripcion(event.target.value)}
          placeholder="Descripción"
        />
      </label>
      <label>
        Género:
        <select value={genero} onChange={(event) => setGenero(event.target.value)}>
          <option value="">Selecciona un género</option>
          <option value="accion">Acción</option>
          <option value="aventura">Aventura</option>
          <option value="pelea">Pelea</option>
        </select>
      </label>
      <label>
        Plataforma:
        <select value={plataforma} onChange={(event) => setPlataforma(event.target.value)}>
          <option value="">Selecciona una plataforma</option>
        
        </select>
      </label>
      <label>
        Imagen:
        <input type="file" onChange={(event) => setImagen(event.target.files[0])} />
      </label>
      <button type="submit">Enviar</button>
    </form>
  );
};
