}
  return (
    <div className={style.containerForm}>
      <h1>Create Video Game</h1>
      <form className={style.containerInputsForm}>
        Name <input value="Name" type="text" name="name"></input>
        Summary <input value="Summary" type="text" name="summary"></input>
