import React, { useState, useE } from "react";
import { useSelector, useDispatch } from "react-redux";
import style from "./Form.module.css";
import Platforms from "../Platforms/Platforms";
import Genres from "../Genres/Genre";
import Validations from "./Validations";

export const Form = () => {
  const allGenres = useSelector((state) => state.genres);
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(LoadGenres()) // dado que se desmonta con el f5
    return(()=>{
      /// por si entro y salio rapido me aseguro tener limpio rta
      console.log('saliendo form');
      dispatch(CleanRta())
      dispatch(CleanFilterGames())
  })
  },[dispatch]);

  

  return (
    <div className={style.container}>
      <form className={style.containerForm}>
        <h1>Create Video Game</h1>
        <div className={style.containerInputs}>
          <span className={style.span}>Name</span>
          <input
            placeholder="Name"
            type="text"
            name="name"
            className={style.inputs}
          ></input>

          <span className={style.span}>Summary</span>
          <input
            placeholder="Summary"
            type="text"
            name="summary"
            className={style.inputs}
          ></input>
          <span className={style.span}>Realease</span>
          <input
            placeholder="Realease"
            type="date"
            name="released"
            className={style.inputs}
          ></input>
          <span className={style.span}>Rating</span>
          <input
            placeholder="Rating"
            type="number"
            name="rating"
            className={style.inputs}
          ></input>
          <span className={style.span}>Description</span>
          <textarea
            name="description"
            placeholder="Description"
            rows="5"
            className={style.inputs}
          ></textarea>

          {/* SELECCIONAR GENERO */}

          <span className={style.span}>Genre</span>
          <select
            name="genre"
            className={`${style.inputs} ${style.textarea}`}
            defaultValue="Select genres"
          >
            <option disabled selected defaultValue="Select Genre">
              Select Genre
            </option>
            <Genres allGenres={allGenres} />
          </select>

          {/* SELECCIONAR PLATAFORMA */}
          <span className={style.span}>Platforms</span>
          <select
            name="platforms"
            className={`${style.inputs} ${style.textarea}`}
            defaultValue="Select platform"
          >
            <option disabled selected defaultValue="Select Platforms">
              Select Platforms
            </option>
            <Platforms />
          </select>
          <button className={style.formBtn}>SEND</button>
        </div>
      </form>
    </div>
  );
};
