import React, {useEffect, useState} from 'react'
import Platforms from '../Platforms/Platforms'
import {useSelector, useDispatch} from 'react-redux'
import {LoadGenres, CleanRta, CleanFilterGames} from '../../redux/actions'
import Genres from '../Genres/Genre'
import style from './Form.module.css'
import { validacionesForm } from './validaciones'


export const Form = () => {

  const dispatch = useDispatch()
  const allGenres = useSelector((state) => state.genres)

  const [error, setError]= useState({})
  const [input, setInput] =useState({
    name: '',
    platform: '',
    description: '',
    date: '',
    rating: '',
    searchGenres: []
  })

  const handleSubmit =(e)=>{
    e.preventDefault()
    setInput()
  }

 
  const handleChangeInput = (e) => {
    const name = e.target.name;
    const value = e.target.value;
    setInput({
      ...input,
      [name]: value
    });
    console.log(input);
  };


  useEffect(() => {
    dispatch(LoadGenres()) //Cargar generos al actualiar pagina
    return () => {
      dispatch(CleanRta())
      dispatch(CleanFilterGames())
    }
  }, [dispatch])

  return (
  <form className={style.container} onSubmit={handleSubmit}>
    <div className={style.title}>
      <h1>Crear VideoJuego</h1>
    </div>
    <div >
      <input type="text" placeholder='Nombre' name='name' className={style.inputs} onChange={handleChangeInput} value={input.name}/>
    </div>
    {/* --------------- */}
    <span></span>
    <div >
      <input type="number" name='rating' placeholder='ClasificaciÃ³n' className={style.inputs} onChange={handleChangeInput} value={input.rating}/>
    </div>
    {/* --------------- */}
    <div >
      <input type="date" name='date' className={style.inputs} onChange={handleChangeInput} value={input.date}/>
    </div>
    {/* --------------- */}
    <div >
      <textarea name="description" id="" cols="25" rows="4" placeholder='DescripciÃ³n' className={style.inputs} onChange={handleChangeInput} value={input.description}>  
      </textarea>
    </div>
    {/* --------------- */}
    <div>
      <select name="platform"onChange={handleChangeInput} value={input.platform}>
      <option hidden>
        Seleccionar plataforma
      </option>

      <Platforms/>
      </select>
    </div>
    {/* --------------- */}
    <div>
      <select name="genres" onChange={handleChangeInput} value={input.genres}>
        
      <option hidden>
        Seleccionar genero
      </option>
      <Genres allGenres={allGenres}/>
      </select>
    </div>

    <div className={style.containerImg} onSubmit={handleChangeInput} value={input.image}>
      <label> Cargar Imagen <input  type="file" name="image" className={style.file}></input> </label>
    </div>

      <div>
        <input className={`${style.btn} ${style.inputs}`} type='submit' value='Crear'/>
      </div>
  </form>
  )
}
