import React, { useState } from "react";
import { useSelector } from "react-redux";
import style from "./Form.module.css";
import Platforms from "../Platforms/Platforms";
import Genres from "../Genres/Genre";
import Validations from "./Validations";

export const Form = () => {
  const allGenres = useSelector((state) => state.genres);

  const [input, setInput] = useState({
    name: "",
    description: "",
    platforms: "",
    date: "",
    rating: "",
    searchGenre: [],
  });

  const handleSubmit =async (event) => {
    event.preventDefault();

    const propertyNames = Object.keys(errors);
    if(!propertyNames.length>0)
    {

      event.preventDefault();
      const data = new FormData();
      data.append('Nombre', input.Nombre);
      data.append('Descripcion', input.Descripcion);
      data.append('Plataformas', input.Plataformas);
      data.append('Fecha_lanzamiento', input.Fecha_lanzamiento);
      data.append('Rating', input.Rating);
      data.append('SearchGenre', input.SearchGenre);
      data.append('image', file);

      try {
        let rta= await axios.post('videogames', data )
          .then(response => {
          return(response.data)
          })
        
          if(rta.length === undefined)
          {
            // se creo o no porque ya existe
            setRta(rta);
            setCargo(false)
          }else
          {
            // no se creo por validaciones
            setValidacion(rta)
            setCargo(true)
          }
        } catch (error) {
          // no se creo error 404
          console.error(error.message)
        }
        
    }// if
  }

  const [errors, seterrors] = useState({});
  const [file, setFile] = useState(null);

  const HandleChangeInput = (event) => {
    // seteo el input

    switch (event.target.name) {
      case "SearchGenre": {
        const selectedValues = [];
        const options = event.target.options;
        //  calcula los options del select que tiene y busca los select true
        for (let i = 0; i < options.length; i++) {
          if (options[i].selected) {
            selectedValues.push(options[i].value);
          }
        }
        setInput({
          ...input,
          SearchGenre: selectedValues,
        });
        break;
      } //
      case "Imagen": {
        setFile(event.target.files[0]);
        break;
      } //
      default: {
        setInput({
          ...input,
          [event.target.name]: event.target.value,
        });
        break;
      }
    }

    seterrors(
      Validations({ ...input, [event.target.name]: event.target.value }, file)
    );
  };

  return (
    <div className={style.container}>
      <form className={style.containerForm}>
        <h1>Create Video Game</h1>
        <div className={style.containerInputs}>
          <span className={style.span}>Name</span>
          <input
            placeholder="Name"
            type="text"
            name="name"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></input>

          <span className={style.span}>Summary</span>
          <input
            placeholder="Summary"
            type="text"
            name="summary"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></input>
          <span className={style.span}>Realease</span>
          <input
            placeholder="Realease"
            type="date"
            name="released"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></input>
          <span className={style.span}>Rating</span>
          <input
            placeholder="Rating"
            type="number"
            name="rating"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></input>
          <span className={style.span}>Description</span>
          <textarea
            name="description"
            placeholder="Description"
            rows="5"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></textarea>

          {/* SELECCIONAR GENERO */}

          <span className={style.span}>Genre</span>
          <select
            name="genre"
            className={`${style.inputs} ${style.textarea}`}
            defaultValue="Select genres"
          >
            <option disabled selected defaultValue="Select Genre">
              Select Genre
            </option>
            <Genres allGenres={allGenres} />
          </select>

          {/* SELECCIONAR PLATAFORMA */}
          <span className={style.span}>Platforms</span>
          <select
            name="platforms"
            className={`${style.inputs} ${style.textarea}`}
            defaultValue="Select platform"
          >
            <option disabled selected defaultValue="Select Platforms">
              Select Platforms
            </option>
            <Platforms />
          </select>
          <button className={style.formBtn} onSubmit={handleSubmit}>SEND</button>
        </div>
      </form>
    </div>
  );
};
