import React, { useState } from 'react';

export const Form = () => {
  // State para manejar los valores del formulario
  const [formulario, setFormulario] = useState({
    nombre: '',
    fecha: '',
    clasificacion: '',
    descripcion: '',
    genero: '',
    plataforma: '',
    imagen: ''
  });

  // Manejar cambios en los inputs
  const handleChange = (e) => {
    setFormulario({
      ...formulario,
      [e.target.name]: e.target.value
    });
  }

  // Manejar envío del formulario
  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes enviar los datos del formulario a través de una API o una función que maneje el envío
    console.log(formulario);
  }

  return (
    <form className="formulario" onSubmit={handleSubmit}>
      <label className="formulario__label">
        <span>Nombre:</span>
        <input type="text" name="nombre" value={formulario.nombre} onChange={handleChange} className="formulario__input" />
      </label>
      <label className="formulario__label">
        <span>Fecha:</span>
        <input type="date" name="fecha" value={formulario.fecha} onChange={handleChange} className="formulario__input" />
      </label>
      <label className="formulario__label">
        <span>Clasificación:</span>
        <input type="text" name="clasificacion" value={formulario.clasificacion} onChange={handleChange} className="formulario__input" />
      </label>
      <label className="formulario__label">
        <span>Descripción:</span>
        <textarea name="descripcion" value={formulario.descripcion} onChange={handleChange} className="formulario__textarea"></textarea>
      </label>
      <label className="formulario__label">
        <span>Género:</span>
        <select name="genero" value={formulario.genero} onChange={handleChange} className="formulario__select">
          <option value="">Seleccione un género</option>
          <option value="accion">Acción</option>
          <option value="aventura">Aventura</option>
          <option value="pelea">Pelea</option>
        </select>
      </label>
      <label className="formulario__label">
        <span>Plataforma:</span>
        <select name="plataforma" value={formulario.plataforma} onChange={handleChange} className="formulario__select">
          <option value="">Seleccione una plataforma</option>
          <option value="PS5">PS5</option>
          <option value="PS5 y XBOX">PS5 y XBOX</option>
        </select>
      </label>
      <label className="formulario__label">
        <span>Imagen:</span>
        <input type="file" name="imagen" onChange={handleChange} className="formulario__file" />
      </label>
      <button type="submit" className="formulario__boton">Enviar</button>
    </form>
  );
}
