import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import style from "./Form.module.css";
import axios from "axios";

import Platforms from "../Platforms/Platforms";
import Genres from "../Genres/Genre";
import Validations from "./Validations";

import {CleanFilterGames,CleanRta,LoadGenres}  from '../../redux/actions'




export const Form = () => {
  const allGenres = useSelector((state) => state.genres);
  const dispatch = useDispatch()

  const [Rta,setRta]=useState({})
  const [Cargo,setCargo]=useState(false)
  const [errors, seterrors] = useState({});
  const [file, setFile] = useState(null);
  const [validation, setValidation]=useState([])

  const [input, setInput] = useState({
    name: "",
    description: "",
    platforms: "",
    date: "",
    rating: "",
    searchGenre: [],
  });


  useEffect(() => {
    dispatch(LoadGenres()) //todo => para cargar los generos
    return(()=>{
      
      console.log('Saliendo del Formulario...');
      dispatch(CleanRta())
      dispatch(CleanFilterGames())
  })
  },[dispatch]);



  const handleSubmit =async (event) => {
    event.preventDefault();

    const propertyNames = Object.keys(errors);
    if(!propertyNames.length>0)
    {

      event.preventDefault();
      const data = new FormData();
      data.append('Nombre', input.Nombre);
      data.append('Descripcion', input.Descripcion);
      data.append('Plataformas', input.Plataformas);
      data.append('Fecha_lanzamiento', input.Fecha_lanzamiento);
      data.append('Rating', input.Rating);
      data.append('SearchGenre', input.SearchGenre);
      data.append('image', file);

      try {
        let rta= await axios.post('videogames', data )
          .then(response => {
          return(response.data)
          })
        
          if(rta.length === undefined)
          {
            // se creo o no porque ya existe
            setRta(rta);
            setCargo(false)
          }else
          {
            // no se creo por validaciones
            setValidation(rta)
            setCargo(true)
          }
        } catch (error) {
          // no se creo error 404
          console.error(error.message)
        }
        
    }// if
  }





  const HandleChangeInput = (event) => {

    switch (event.target.name) {
      case "SearchGenre": {
        const selectedValues = [];
        const options = event.target.options;
        //  calcula los options del select que tiene y busca los select true
        for (let i = 0; i < options.length; i++) {
          if (options[i].selected) {
            selectedValues.push(options[i].value);
          }
        }
        setInput({
          ...input,
          SearchGenre: selectedValues,
        });
        break;
      } //
      case "Imagen": {
        setFile(event.target.files[0]);
        break;
      } //
      default: {
        setInput({
          ...input,
          [event.target.name]: event.target.value,
        });
        break;
      }
    }

    seterrors(
      Validations({ ...input, [event.target.name]: event.target.value }, file)
    );
  };

  return (
    <div className={style.container}>
      <form className={style.containerForm} onSubmit={handleSubmit}>
        <h1>Create Video Game</h1>
        <div className={style.containerInputs}>
          <span className={style.span}>Nombre</span>
          <input
            placeholder="Nombre"
            type="text"
            name="name"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></input>
          {errors.name && <div> {errors.name}</div>}
          <span className={style.span}>Fecha</span>
          <input
            placeholder="Fecha de lanzamiento"
            type="date"
            name="released"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></input>
          {/* {errors.date && <div> {errors.date}</div>} */}

          <span className={style.span}>Clasificacion</span>
          <input
            placeholder="ClasificaciÃ³n"
            type="number"
            name="rating"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></input>
          {errors.rating && <div> {errors.rating}</div>}
          
          <span className={style.span}>Descripcion</span>
          <textarea
            name="description"
            placeholder="Descripcion"
            rows="5"
            className={style.inputs}
            onChange={HandleChangeInput}
          ></textarea>
          {errors.description && <div> {errors.description}</div>}

          {/* SELECCIONAR GENERO */}

          <span className={style.span}>Genero</span>
          <select
            name="genre"
            className={`${style.inputs} ${style.textarea}`}
            defaultValue="Select genres"
          >
            <option disabled selected defaultValue="Select Genre">
              Seleccion de generos
            </option>
            <Genres allGenres={allGenres} />
          </select>
          {errors.genres && <div> {errors.genres}</div>}

          {/* SELECCIONAR PLATAFORMA */}
          <span className={style.span}>Plataforma</span>
          <select
            name="platforms"
            className={`${style.inputs} ${style.textarea}`}
            defaultValue="Select platform"
          >
            <option disabled selected defaultValue="Select platforms" >
              Seleccion de plataformas
            </option>
            <Platforms />
          </select>
          {/* {errors.platforms && <div> {errors.platforms}</div>} */}
          <input name="Imagen" type="file" accept="image/jpeg"  onChange={HandleChangeInput} />
          <div className={style.Error}>{errors.f && <div> {errors.file}</div>}
      </div>
          <input type="submit" className={style.formBtn}  value='Crear'/>
        </div>
      </form>
    </div>
  );
};
