import React, { useState } from "react";
import axios from "axios";
import style from './Form.module.css'

export const Form = () => {
  const [input, setInput] = useState({
    name: '',
    description: '',
    platforms: '',
    date: '',
    rating: '',
    SearchGenres: [],
    image: null,
  });


  const handleImageChange = (e) => {
    setInput(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", input.name);
    formData.append("description", input.description);
    formData.append("platforms", input.platforms);
    formData.append("image", input.image);
    formData.append("date", input.date);
    formData.append("rating", input.rating);
    formData.append("SearchGenre", input.searchGenre);

    try {
      const response = await axios.post("/videogames", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <form onSubmit={handleSubmit} className={style.container}>
      <label>
        Name:
        <input
          type="text"
          value={input.name}
          onChange={(e) => setInput(e.target.value)} 
          className={style.inputs}
        />
      </label>
      <br />
      <label>
        Description:
        <textarea
          value={input.description}
          onChange={(e) => setInput(e.target.value)} 
          className={style.inputs}
        ></textarea>
      </label>
      <br />
      <label>
        Platforms:
        <input
          type="text"
          value={input.platforms}
          onChange={(e) => setInput(e.target.value)} 
          className={style.inputs}
        />
      </label>
      <br />
      <label>
        Image:
        <input type="file" onChange={handleImageChange} 
        className={style.inputs} />
      </label>
      <br />
      <label>
        Date:
        <input
          type="date"
          value={input.date}
          onChange={(e) => setInput(e.target.value)} 
          className={style.inputs}
        />
      </label>
      <br />
      <label>
        Rating:
        <input
          type="number"
          value={input.rating}
          onChange={(e) => setInput(e.target.value)} 
          className={style.inputs}
        />
      </label>
      <br />
      <div>
        <input
          type="text"
          value={input.SearchGenres}
          onChange={(e) => setInput(e.target.value)} 
          className={style.inputs}
        />
      </div>
      <button type="submit" className={style.inputs}>Create</button>
    </form>
  );
};
