import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import style from "./Form.module.css";
import axios from "axios";

import Platforms from "../Platforms/Platforms";
import Genres from "../Genres/Genre";
import Validations from "./Validations";
import validation from './validation'

import { CleanFilterGames, CleanRta, LoadGenres } from "../../redux/actions";

export const Form = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [platforms, setPlatforms] = useState([]);
  const [image, setImage] = useState('');
  const [date, setDate] = useState('');
  const [rating, setRating] = useState(0);
  const [genres, setGenres] = useState([]);

  const handleNameChange = (event) => setName(event.target.value);
  const handleDescriptionChange = (event) =>
    setDescription(event.target.value);
  const handlePlatformsChange = (event) =>
    setPlatforms(event.target.value.split(','));
  const handleImageChange = (event) => setImage(event.target.value);
  const handleDateChange = (event) => setDate(event.target.value);
  const handleRatingChange = (event) => setRating(Number(event.target.value));
  const handleGenresChange = (event) =>
    setGenres(event.target.value.split(','));

  const handleSubmit = async (event) => {
    event.preventDefault();

    const result = await createVideoGame({
      name,
      description,
      platforms,
      image,
      date,
      rating,
      searchByGenre: genres,
    });

    if (result.error) {
      alert(result.error);
    } else {
      alert(result.ok);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" value={name} onChange={handleNameChange} />
      </label>
      <br />
      <label>
        Description:
        <textarea value={description} onChange={handleDescriptionChange} />
      </label>
      <br />
      <label>
        Platforms:
        <input type="text" value={platforms} onChange={handlePlatformsChange} />
      </label>
      <br />
      <label>
        Image:
        <input type="text" value={image} onChange={handleImageChange} />
      </label>
      <br />
      <label>
        Release Date:
        <input type="date" value={date} onChange={handleDateChange} />
      </label>
      <br />
      <label>
        Rating:
        <input
          type="number"
          min="0"
          max="10"
          step="0.1"
          value={rating}
          onChange={handleRatingChange}
        />
      </label>
      <br />
      <label>
        Genres (separated by commas):
        <input type="text" value={genres} onChange={handleGenresChange} />
      </label>
      <br />
      <button type="submit">Create Game</button>
    </form>
  );
};
