export const validacionesForm = (input, file) => {
    const errors = {}
  
  
    
    if (!(input.name) || (input.name.length < 4 )) {
      errors.name = 'El nombre debe ser mayor a 4 caracteres'
    }

    if (!(input.rating) || (input.rating.length < 1)) {
        errors.rating = 'Debe agregar una puntuacion'
      }

      if (!(input.date) || (input.date.length < 9)) {
        errors.date = 'Debe seleccionar una fecha'
      }
  
    if (!(input.description) || (input.description.length < 10)) {
      errors.description = 'Descripcion debes tener ser mayor a 10 caracteres'
    }
  
   
  
    if (!(input.platforms) || (input.platforms.length < 1)) {
      errors.platforms = 'Debe seleccionar al menos una plataforma'
    }
  
    if (!(input.genres) || (input.genres.length < 2)) {
      errors.genres = 'Debe seleccionar al menos un genero'
    }
  
    if (!(input.image) && (file === null)) {
      // controla campo vacio y que no se haya cargado
      errors.image = 'Debe cargar una imagen'
    }
  
    return errors
  }
