export const validacionesForm  = (input, file) => {
    let error = {};
    const { name, rating, date, description, platforms, genres, image } = input;
  
    if (!name || name.trim().length < 4) {
      error.name = "El nombre debe ser mayor a 4 caracteres";
    }
  
    if (!rating || rating.trim().length < 1) {
      error.rating = "Debe agregar una puntuacion";
    }
  
    if (!date || date.length < 9) {
      error.date = "Debe seleccionar una fecha valida";
    }
  
    if (!description || description.trim().length < 10) {
      error.description = "Descripcion debe tener al menos 10 caracteres";
    }
  
    if (!platforms || platforms.length < 1) {
      error.platforms = "Debe seleccionar al menos una plataforma";
    }
  
    if (!genres || genres.length < 2) {
      error.genres = "Debe seleccionar al menos un genero";
    }
  
    if (!image && file === null) {
      error.image = "Debe cargar una imagen";
    }
  
    const isValid = Object.keys(errors).length === 0;
  
    return { isValid, errors };
  };
