import React, { useState } from "react";

import { useDispatch, useSelector } from "react-redux";
import {
  getAllGames,
  ResetFilterGames,
  FilterByType,
  OrderGames,
  FilterByGenres,
} from "../../redux/actions";
import Genres from "../Genres/Genre";

import style from "./SearchBar.module.css";

const SearchBar = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState({ name: "" });
  const [Select, setSelect] = useState({
    selectTipo: "filter",
    selectFilter: "filter",
    selectOrden: "filter",
  });
  let allGenres = useSelector((state) => state.genres);

  const HandleSubmit = (e) => {

    e.preventDefault();
    let valor = name.name;
    setName({ name: "" });
    if (valor.length > 0) {
      dispatch(getAllGames(valor));
    } else {
      dispatch(ResetFilterGames());
    }
  };

  const HandleChangeForm = (e) => {
    // seteo el input
    setName({
      ...name,
      [e.target.name]: e.target.value,
    });
  };

  const HandleChangeTipo = (e) => {
    // Para seleccionar por tipo
    setSelect({
      ...Select,
      [e.target.name]: e.target.value,
    });
    dispatch(FilterByType(e.target.value));
  };

  const HandleChangeOrdenar = (e) => {
    // select de tipo
    setSelect({
      ...Select,
      [e.target.name]: e.target.value,
    });
    dispatch(OrderGames(e.target.value));
  };

  const HandleChangeGenero = (e) => {
    // select de Genres
    setSelect({
      ...Select,
      [e.target.name]: e.target.value,
    });
    dispatch(FilterByGenres(e.target.value));
  };

  const HandleClickLimpiar = () => {
    setSelect({
      selectTipo: "filter",
      selectFilter: "filter",
      selectOrden: "filter",
    });
    dispatch(ResetFilterGames());
  };

  return (
    <div className={style.EnLinea}>
      {/* Ordenar por tipo */}
      <div className={style.Margin15}>
        <select
          name="selectTipo"
          value={Select.selectTipo}
          onChange={HandleChangeTipo}
        >
          <option value="filter" disabled="disabled">
            Filtro Tipo
          </option>
          <option value="Db">DB</option>
          <option value="api">API</option>
        </select>

       {/* Filtrar por genero */}
        <select
          name="selectFilter"
          value={Select.selectFilter}
          onChange={HandleChangeGenero}
        >
          <option value="filter" disabled="disabled">
            Filtro Genero
          </option>
          <Genres allGenres={allGenres} />
        </select>

      {/* Ordenar Ascendente y Descendente */}
        <select
          name="selectOrden"
          value={Select.selectOrden}
          onChange={HandleChangeOrdenar}
        >
          <option value="filter" disabled="disabled">
            Ordenar
          </option>
          <option value="ascendente">Ascendente</option>
          <option value="descendente">Descendente</option>
        </select>
      </div>
      <form onSubmit={HandleSubmit}>
        <input
          placeholder="Buscar Juego"
          name="name"
          value={name.name}
          onChange={HandleChangeForm}
          type="text"
        />

        <button type="submit">Buscar</button>
      </form>
      <button className={style.btn} onClick={HandleClickLimpiar}>
        limpiar Filtro
      </button>
    </div>
  );
};

export default SearchBar;
