import React, { useState } from "react";
import axios from "axios";

export const Form = () => {
  const [input, setInput] = useState({
    name: '',
    description: '',
    platforms: '',
    date: '',
    rating: '',
    SearchGenre: [],
    image: null,
  });


  const handleImageChange = (event) => {
    set(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("name", input.name);
    formData.append("description", input.description);
    formData.append("platforms", input.platforms);
    formData.append("image", input.image);
    formData.append("date", input.date);
    formData.append("rating", input.rating);
    formData.append("SearchGenre", input.searchGenre);

    try {
      const response = await axios.post("/create-video-game", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      console.log(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input
          type="text"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
      </label>
      <br />
      <label>
        Description:
        <textarea
          value={description}
          onChange={(event) => setDescription(event.target.value)}
        ></textarea>
      </label>
      <br />
      <label>
        Platforms:
        <input
          type="text"
          value={platforms}
          onChange={(event) => setPlatforms(event.target.value)}
        />
      </label>
      <br />
      <label>
        Image:
        <input type="file" onChange={handleImageChange} />
      </label>
      <br />
      <label>
        Date:
        <input
          type="date"
          value={date}
          onChange={(event) => setDate(event.target.value)}
        />
      </label>
      <br />
      <label>
        Rating:
        <input
          type="number"
          value={rating}
          onChange={(event) => setRating(event.target.value)}
        />
      </label>
      <br />
      <label>
        Search Genre:
        <input
          type="text"
          value={searchGenre}
          onChange={(event) => setSearchGenre(event.target.value)}
        />
      </label>
      <br />
      <button type="submit">Create</button>
    </form>
  );
};
