import { useState } from "react";
import axios from "axios";

export const  Form = () => {
  const [name, setName] = useState("");
  const [description, setDescription] = useState("");
  const [platforms, setPlatforms] = useState("");
  const [image, setImage] = useState("");
  const [date, setDate] = useState("");
  const [rating, setRating] = useState("");
  const [genres, setGenres] = useState([]);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [error, setError] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", name);
    formData.append("description", description);
    formData.append("platforms", platforms);
    formData.append("date", date);
    formData.append("rating", rating);
    formData.append("image", image);
    formData.append("SearchGenre", selectedGenres.join(","));
    try {
      const response = await axios.post("/videogames", formData);
      console.log(response.data);
      // Aquí puedes hacer algo en respuesta al éxito de la solicitud
    } catch (error) {
      console.error(error);
      setError(error.message);
    }
  };

  const handleGenresChange = (e) => {
    const { value } = e.target;
    setSelectedGenres((prevState) =>
      prevState.includes(value)
        ? prevState.filter((genre) => genre !== value)
        : [...prevState, value]
    );
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Nombre</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
      </div>
      <div>
        <label>Imagen</label>
        <input type="file" onChange={(e) => setImage(e.target.files[0])} />
      </div>
      <div>
        <label>Descripción</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
      </div>
      <div>
        <label>Plataformas</label>
        <input type="text" value={platforms} onChange={(e) => setPlatforms(e.target.value)} />
      </div>
      <div>
        <label>Fecha de lanzamiento</label>
        <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
      </div>
      <div>
        <label>Rating</label>
        <input type="text" value={rating} onChange={(e) => setRating(e.target.value)} />
      </div>
      <div>
        <label>Géneros</label>
        <select multiple value={selectedGenres} onChange={handleGenresChange}>
          {genres.map((genre) => (
            <option key={genre} value={genre}>
              {genre}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Crear Juego</button>
      {error && <div>{error}</div>}
    </form>
  );
};
